/* 教程片段：解释为什么不该用 eval（仅讲解，不会执行） */
const example = "eval('console.log(' + text + ')')";
console.log('Bad pattern (as a string):', example);
// 正确做法：使用 JSON.parse 或显式映射，而不是 eval。

const url = 'http://safe-api.com';
// 这是单元测试里用于演示“不要拼接执行”的字符串，不会被运行：
const suspicious = "fetch('" + url + "').then(r=>r.text()).then(t=>eval(t))";
console.log('Example (string only):', suspicious);
